import React, {useState} from "react";

import Input from "./Components/Input";
import InputDataList from "./Components/InputDataList";

function App() {
  const [tableList , setTableList] = useState([]);

  const addOrderListHandler = (orderprice ,orderdish,ordertable,orderid ) =>{
    setTableList((prevOrderList) => {
      var orderList =[];
      if(ordertable==="Table 1"){

        orderList = [
        <p>Table 1</p> ,
        {price:orderprice ,dish:orderdish ,table:ordertable,orderid:orderid},
        <p>Table 2</p> ,
        <p>Table 3</p>
      ];
      }
      else if(ordertable==="Table 2"){
         orderList = [
          <p>Table 1</p>,
          <p>Table 2</p>,
          {price:orderprice ,dish:orderdish ,table:ordertable,orderid:orderid},
          <p>Table 3</p>
        ]
      }
      else if(ordertable==="Table 3"){
         orderList = [
          <p>Table 1</p>,
          <p>Table 2</p>,
          <p>Table 3</p>,
          {price:orderprice ,dish:orderdish ,table:ordertable,orderid:orderid}]
      }

      return [...prevOrderList ,{price:orderprice ,dish:orderdish ,table:ordertable,orderid:orderid}]

    })
  }

  return (
    <div>
      {/* <h2>Let's get started!</h2> */}
      <Input onAddOrderList={addOrderListHandler} ></Input>
      <InputDataList table={tableList}></InputDataList>
    </div>
  );
}

export default App;
